-- UPDATE ESTRUTURAORGANIZACIONAL PARA COLOCAR A ESTRUTURAORGANIZACIONALRESPONSAVEL
DO $$
DECLARE
  v_eoid  numeric;
  v_eorid numeric;
BEGIN
  FOR v_eoid, v_eorid IN SELECT eo.id eoid, eor.id eorid FROM public.ESTRUTURAORGANIZACIONALRESPONSAVEL eor inner join public.ESTRUTURAORGANIZACIONAL eo on eor.ESTRUTURAORGANIZACIONAL_ID = eo.id
  LOOP
     UPDATE public.ESTRUTURAORGANIZACIONAL SET ESTRUTURAORGANIZACIONALRESPONSAVEL_ID = v_eorid where id = v_eoid;
  END LOOP;
END$$;

